from CodeTestLib import PalindromeFinder


def test_simple_results():
    result = PalindromeFinder.locate_palindromic_substring("Ilikeracecarsthatgofast")
    assert result == "racecar"


def test_case():
    result = PalindromeFinder.locate_palindromic_substring(
        """"""
    )
    assert result == "ranynar"
